import React from 'react';
import './menu-item.component.styles.scss';
import { useHistory, useRouteMatch, withRouter } from 'react-router';

const MenuItem=({title,imageUrl,size,linkUrl})=>{

    const history = useHistory()
    const match = useRouteMatch('/shop/:collectionId')

    return (
     <div className={`${size} menu-item`} onClick={()=>history.push(`${match}${linkUrl}`)}>
         <div style={{backgroundImage:`url(${imageUrl})`}} className="background-image"/>
        <div className="content">
          <h1 className="title">{title.toUpperCase()}</h1>
          <span className="subtitle">SHOP NOW</span>   
    </div>
</div>
 )
}

export default withRouter(MenuItem);  //make menu component as Higher order functiton
