import React from 'react'
import {Route} from 'react-router-dom'

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionsPage from '../../pages/collections/collections.component';

import { firestore, convertCollectionSnapshotToMap } from '../../firebase/firebase.utill';


class ShopPage extends React.Component{
    unSubscribeFromShapshop = null;

    componentDidMount(){
        const colletionRef = firestore.collection('colletions')

        colletionRef.onSnapshot(async snapShot => {
            convertCollectionSnapshotToMap(snapShot)
        })
    }

    render(){ 
        const {match} = this.props
    return(
    <div className="shop-list">
        <Route exact path={`${match.path}`} component={CollectionsOverview}/>
        <Route path={`${match.path}/:collectionId`} component={CollectionsPage}/>
    </div>
    )}
}

export default ShopPage
