import { TOGGLE_DROPDOWN } from "./carttypes"

const initialSTate = {
    hidden: false
}

const cartReduers = (state = initialSTate, action) =>{

    switch(action.type){
        case TOGGLE_DROPDOWN: 
            return{
                ...state,
                hidden: !state.cartDropdown
            }
        default:{
            return state
        }
    }
}

export default cartReduers
