import './App.css';
import Homepage from './pages/homepage/homepage.component';
import ShopList from './components/shop-list/shop.component';
import Header from './components/Header/header.component';
import {Switch,Route} from 'react-router-dom'
import Signinandsignup from './pages/sign-in-sign-out/sign-in-sign-out.component';
import React from 'react';
import { auth, createUserProfileDocument } from './firebase/firebase.utill';

const Hatspage=()=><h1>Hats Page</h1>
const Menspage=()=><h1>Mens Page</h1>
const Womenspage=()=><h1>Womens Page</h1>


class App extends React.Component {

  constructor(){
    super();
    this.state={
      currentuser:null,

    }
  }

  unsubscribefromauth = null;

  componentDidMount(){
    this.unsubscribefromauth=auth.onAuthStateChanged(async userAuth=>{

      if(userAuth){
         const userRef = await createUserProfileDocument(userAuth)

         userRef.onSnapshot(Snapshot=>{
           this.setState({
             currentuser: {
               id:Snapshot.id,
             ...Snapshot.data()
             }
           },()=>console.log(this.state))
         })
       }
       else{
       this.setState({currentuser:userAuth})
       }
       }
    )

  }

  componentWillUnmount(){
    this.unsubscribefromauth();
  }

  render(){
  return (
    <div>
      <Header currentuser={this.state.currentuser}/>
      <Switch>
      <Route exact path='/' component={Homepage}/>
      <Route path='/hats' component={Hatspage}/>
      <Route path='/mens' component={Menspage}/>
      <Route path='/womens' component={Womenspage}/>
      <Route path='/shop' component={ShopList}/>
      <Route path='/signin' component={Signinandsignup}/>
      </Switch>
    </div>
  );
}
}

export default App;
