import React from 'react';
import FormInput from '../../components/form-input/form-input.component';
import Custombtn from '../../components/custom-btn/custom-btn.component';

class SignIn extends React.Component{
    constructor(){
        super();
        this.state={
            email:'',
            password:''
        }
    }

    handlesubmit=(e)=>{
         e.preventDefault()
         this.setState({email:'',password:''})
    }

    handlechange=(e)=>{
        const {name,value}=e.target;

        this.setState({[name]:value})
    }

    render(){
        const {email,password}=this.state;
        return(
            <div className="sign-in">
                <h2>I already have an account!</h2>
                <span>sign in with your email and password</span>
            <form onSubmit={this.handlesubmit}>
                <FormInput 
                type="email" 
                value={email} handlechange={this.handlechange} name="email"/>
                <label>Email</label>
                <FormInput type="password" value={password}  handlechange={this.handlechange} name="password"/>
                <label>Password</label>
                <Custombtn type="submit" value="Submit Form"/>
            </form>
            </div>
        )
    }
}

export default SignIn;
