import React from 'react'
import './collection-item.styles.scss'
import Custombtn from '../custom-btn/custom-btn.component';
import {connect} from 'react-redux'
import { newItem } from '../../redux/cart/cartactions';

const Preview=(item,props)=>{
     const {name, price, imageUrl} = item
    return(
    <div className="collection-item">
     <div style={{backgroundImage:`url(${imageUrl})`}} className="image"/>

     <div className="collection-footer">
        <span className="name">{name}</span>
        <span className="price">{price}</span>
        </div>
        <div onClick={()=>props.addItem(item)}>
        <Custombtn cart>Add to cart</Custombtn>
        </div>
    </div>)
}

const mapDispatchToProps = dispatch =>({
        addItem : (item)=> dispatch(newItem(item))
})

export default connect(null, mapDispatchToProps)(Preview);
