import { createSelector } from "reselect";
import { memoize } from "lodash";
const COLLECTION_ID_MAP = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  womens: 4,
  mens: 5
};

const selectShop = state => state.shop;

export const selectCollections = createSelector([selectShop], shop => shop.collections);
export const selectCollection = memoize(collectionUrlParams => createSelector([selectCollections], collections => collections.find(collections => collections.id === COLLECTION_ID_MAP[collectionUrlParams])));
