import React from 'react'; 
import {auth, createcreateUserProfileDocument} from '../../firebase/firebase.utill';

import Custombtn from '../../components/custom-btn/custom-btn.component';
import FormInput from '../../components/form-input/form-input.component';

import './sign-up.styles.scss'

class Signup extends React.Component{
        constructor(){
            super()

            this.state={
                displayName:'',
                email:'',
                password:'',
                c_password:''
            }
        }

        handlesubmit=(e)=>{
            e.preventDefault();
            const {displayName, email, password, c_password} = this.state

            if(password !== c_password){
                return alert("Password and Confirm password are not same")
            }

            this.setState({
                displayName:'',
                email:'',
                password:'',
                c_password:''
            })
        }

        handlechange=(e)=>{
            const {name, value}= e.target;

            this.setState({
                [name]:value
            })
        }

        render(){
            const {displayName, email, password, c_password} = this.state
            return(
                <div className="sign-up">
                <h2 className="tiitle">I dont have an account</h2>
                <span>Create account</span>

                <form onSubmit={this.handlesubmit}>

                    <FormInput
                     type="text"
                     name="displayName"
                     value={displayName}
                     handlechange={this.handlechange}
                     label="Display Name"
                     />

                     <FormInput
                     type="email"
                     name="email"
                     value={email}
                     handlechange={this.handlechange}
                     label="Email"
                     />

                     <FormInput 
                     type="password"
                     name="password"
                     value={password}
                     handlechange={this.handlechange}
                     label="Password"
                     />

                     <FormInput
                     type="password"
                     name="c_password"
                     value={c_password}
                     handlechange={this.handlechange}
                     label="Confirm password"
                     />

                     <Custombtn type="submit">Sign up</Custombtn>

                </form>

                </div>
            )
        }

}

export default Signup;
