import { TOGGLE_DROPDOWN } from "./carttypes";
import { NEW_ITEM } from "./carttypes";
import { REMOVE_ITEM } from "./carttypes";

function toggleCartDropdown() {
    return {
        type: TOGGLE_DROPDOWN
    }
}

export const newItem = item => ({
        type: NEW_ITEM,
        payload: item
    })


export const removeItem = item => ({
    type:REMOVE_ITEM,
    payload: item
})

export default toggleCartDropdown
