import React from 'react'
import {Route} from 'react-router-dom'

import { createStructuredSelector } from 'reselect';
import CollectionsPage from '../../pages/collections/collections.component';

import fetchCollectionsAsync from '../../redux/shop/shop.actions'

import { selectIsCollectionIsLoaded } from '../../redux/shop/shop.selector';

import { connect } from 'react-redux';

import WithSpinner from '../with-spinner/with-spinner.component';
import collectionsOverviewComponent from '../../components/collections-overview/collection-overview.container';

const CollectionsPageWithSpinner = WithSpinner(CollectionsPage)

class ShopPage extends React.Component{
    componentDidMount(){
       const {fetchCollectionsAsync} = this.props
       fetchCollectionsAsync()
    }

    render(){ 
        const {match, isCollectionIsLoaded} = this.props
    return(
    <div className="shop-list">
        <Route exact 
         path={`${match.path}`} 
         component = {collectionsOverviewComponent} 
        />

        <Route path={`${match.path}/:collectionId`} 
        render = {
            (props) => <CollectionsPageWithSpinner isLoading={isCollectionIsLoaded} {...props}/>
        }
        />
    </div>
    )}
}

const mapStateToProps = createStructuredSelector({
    isCollectionIsLoaded: selectIsCollectionIsLoaded
})

const mapDispatchToProps =  dispatch  => ({
    fetchCollectionsAsync: () => dispatch(fetchCollectionsAsync())
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
